mapper.select:
SELECT * FROM USERS
WHERE email = #{email}
  AND provider = 'local'

SELECT COUNT(*) > 0
FROM USERS
WHERE nickname = #{nickname}

mapper.update:
UPDATE USERS
SET password = #{password}
WHERE email = #{email}

UPDATE USERS
SET nickname = #{nickname}
WHERE user_id = #{userId}

UPDATE USERS
SET refresh_token = #{refreshToken},
    rt_expired_date = #{expiredAt}
WHERE user_id = #{userId}

UPDATE USERS
SET provider = #{provider}, provider_id = #{providerId}
WHERE user_id = #{userId}

mapper.delete:
DELETE refresh_token, rt_expired_date
FROM USERS
WHERE user_id = #{userId}
